mapper.insert:
insert into t_invoice_package(
id , date , user_id , count
) values (
#{id} , #{date} , #{userId} , #{count}
)

mapper.select:
select id,
       date,
       count
  from t_invoice_package


        and user_id = #{userId}


        and date >= #{startTime}


        and date <= #{endTime}


order by date desc
